                        agent_rep=ocomp,
                        # features=[ia.input_port],
                        features=[ocomp.input_CIM.output_ports[0]],
                        name="oController",
                        objective_mechanism=pnl.ObjectiveMechanism(
                                monitor=ib.output_port,
                                function=pnl.SimpleIntegrator,
                                name="oController Objective Mechanism"
                        ),
